var _generator=require('../../libs/generator');var _simulator=require('../../libs/simulator');describe('match simulator tests',function(){test('simulates a game between returning result',function(){var home=_generator.generator.team();var away=_generator.generator.team();var result=_simulator.match.simulate(home,away);expect(result).toEqual({home:expect.any(String),away:expect.any(String),winner:expect.any(String),loser:expect.any(String),isDraw:expect.any(Boolean),scorers:expect.anything(),homeGoal:expect.any(Number),awayGoal:expect.any(Number)});if(!result.isDraw){expect(result.homeGoal>0||result.awayGoal>0).toBe(true);}});});describe('round simulator tests',function(){test('simulates a round match by match',function(){var teams=_generator.generator.teams(4);var fixture=_generator.fixtureGenerator.generate(teams);var matches=fixture.pop().matches;var results=_simulator.round.simulate(matches,teams);expect(results.length).toBe(teams.length/2);});});